class Uppload extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.fileInput = React.createRef();

  }
  // handleChange(e) {
  //   const files = e.target.files;
  //   if (files && files[0]) this.props.handleFile(files[0]);
  // }

  handleChange(info) {
    if (info.file.status !== 'uploading') {
      console.log(info.file, info.fileList);
    }
    if (info.file.status === 'done') {
      message.success(`${info.file.name} file uploaded successfully`);
    } else if (info.file.status === 'error') {
      message.error(`${info.file.name} file upload failed.`);
    }
  }

  beforeUpload(file, fileList) {
    var rABS = true;
    const f = fileList[0];
    var reader = new FileReader();
    reader.onload = function (e) {
      var data = e.target.result;

      var workbook = XLSX.read(data, {
        type: rABS ? 'binary' : 'array'
      });
      var first_worksheet = workbook.Sheets[workbook.SheetNames[0]];
      var jsonArr = XLSX.utils.sheet_to_json(first_worksheet, { header: 1 });

      let resultArray = [];
      console.log(jsonArr);
      console.log(jsonArr.length);
      console.log(jsonArr[0].length);
      for (let i = 1; i < jsonArr.length; i++) {
        var objArray = [];
        for (let j = 0; j < jsonArr[0].length; j++) {
          var obj = new Object;
          obj = { key: jsonArr[0][j], value: jsonArr[i][j] };
          objArray.push(obj);
        }
        resultArray.push(objArray);

      }
      console.log(resultArray);

      this.props.onUpload?.(resultArray);

      // http.post("/prejson", resultArray);

    };
    if (rABS) reader.readAsBinaryString(f); else reader.readAsArrayBuffer(f);
    return false;
  }

  render() {

    return (

      <Upload
        accept="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
        name="file"
        headers={{
          authorization: 'authorization-text',
        }}
        onChange={this.handleChange}
        beforeUpload={this.beforeUpload}
      >
        <Button icon={<UploadOutlined />}>Click to Upload</Button>
        {/* <input
          type="file"
          hidden
          accept={SheetJSFT}
          ref={this.fileInput}
          onChange={this.handleChange}

        /> */}
      </Upload>
    )
  }

}

