import React, { useContext, useState, useEffect, useRef } from 'react';
import { Table, Input, Button, Popconfirm, Form, Select } from 'antd';
const { Option } = Select;

const EditableContext = React.createContext(null);

const EditableRow = ({ index, ...props }) => {
    const [form] = Form.useForm();
    return (
        <Form form={form} component={false}>
            <EditableContext.Provider value={form}>
                <tr {...props} />
            </EditableContext.Provider>
        </Form>
    );
};

const EditableCell = ({
    title,
    editable,
    children,
    dataIndex,
    record,
    handleSave,
    ...restProps
}) => {
    const [editing, setEditing] = useState(false);
    const inputRef = useRef(null);
    const form = useContext(EditableContext);
    useEffect(() => {
        if (editing) {
            inputRef.current.focus();
        }
    }, [editing]);

    const toggleEdit = () => {
        setEditing(!editing);
        form.setFieldsValue({
            [dataIndex]: record[dataIndex],
        });
    };

    const save = async () => {
        try {
            const values = await form.validateFields();
            toggleEdit();
            handleSave({ ...record, ...values });
        } catch (errInfo) {
            console.log('Save failed:', errInfo);
        }
    };
    function handleChange(value) {
       let val = value;
      }

    let childNode = children;

    if (editable) {
        childNode = editing ? (
            <Form.Item
                style={{
                    margin: 0,
                }}
                name={dataIndex}
                rules={[
                    {
                        required: true,
                        message: `${title} is required.`,
                    },
                ]}
            >
                <Input ref={inputRef} onPressEnter={save} onBlur={save} />
            </Form.Item>
        ) : (
            <div
                className="editable-cell-value-wrap"
                style={{
                    paddingRight: 24,
                }}
                onClick={toggleEdit}
            >
                {children}
            </div>
        );
    }

    return <td {...restProps}>{childNode}</td>;
};

class UpTable extends React.Component {
    constructor(props) {
        super(props);

        this.columns = [
            {
                title: 'Fund',
                dataIndex: 'name',
                width: '30%',
                render: () =>
                    <div className="site-input-group-wrapper">
                        <Select defaultValue="Abc">
                            <Option value="Abc">Abc</Option>
                            <Option value="123">123</Option>
                            <Option value="%">%</Option>

                        </Select>
                    </div>
            },
            {
                title: 'Document type',
                dataIndex: 'DocumentType',
                render: () =>
                    <div className="site-input-group-wrapper">
                        <Select defaultValue="CapitalCall" onChange={handleChange}>
                            <Option value="CapitalCall">Capital Call</Option> //Numeric
                <Option value="CapitalAccount">Capital Account</Option> //Periodic
                <Option value="CatchUpCall">Catch-Up Call</Option> //Numeric 
                <Option value="K1">K-1</Option> //Periodic

            </Select>
                    </div>
            },
            {
                title: 'Periodic or Numeric Document?',
                dataIndex: 'PeriodicorNumeric',
                render() {
                    return (
                        <div>
                            {(() => {
                                if (onChange =="CapitalCall") {
                                    return (
                                        <div>Numeric</div>
                                    )
                                } else if (onChange =="CatchUpCall") {
                                    return (
                                        <div>Numeric</div>
                                    )
                                } else if (onChange =="CapitalAccount") {
                                    return (
                                        <div>Periodic</div>
                                    )
                                }else if (onChange =="K1") {
                                    return (
                                        <div>Periodic</div>
                                    )
                                }
                            })()}
                        </div>

                    )

                    // <div className="site-input-group-wrapper">


                    //     <Select defaultValue="Periodic">
                    //         <Option value="Periodic">Periodic</Option>
                    //         <Option value="Numeric">Numeric</Option>

                    //     </Select>
                    // </div>
                }
            },
            {
                title: 'Document Number',
                dataIndex: 'DocumentNumber',
                render: () =>
                    <div className="site-input-group-wrapper">
                        <Select defaultValue="1">
                            <Option value="Periodic">Periodic</Option>
                            <Option value="Numeric">Numeric</Option>

                        </Select>
                    </div>
            },
            {
                title: 'Name the ',
                dataIndex: 'DocumentName',
                editable: true,
            },

        ];
        this.state = {
            dataSource: [
                {

                },

            ],
            count: 1,
        };
    }


    handleSave = (row) => {
        const newData = [...this.state.dataSource];
        const index = newData.findIndex((item) => row.key === item.key);
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        this.setState({
            dataSource: newData,
        });
    };

    render() {
        const { dataSource } = this.state;
        const components = {
            body: {
                row: EditableRow,
                cell: EditableCell,
            },
        };
        const columns = this.columns.map((col) => {
            if (!col.editable) {
                return col;
            }

            return {
                ...col,
                onCell: (record) => ({
                    record,
                    editable: col.editable,
                    dataIndex: col.dataIndex,
                    title: col.title,
                    handleSave: this.handleSave,
                }),
            };
        });
        return (
            <div>
                <Table
                    components={components}
                    rowClassName={() => 'editable-row'}
                    bordered
                    dataSource={dataSource}
                    columns={columns}
                    pagination={{ defaultPageSize: 10, hideOnSinglePage: true }}
                    style={{
                        margin: 20,
                    }}
                />
            </div>
        );
    }
}
export default UpTable;
