import React, { Component } from 'react';
import MainInput from '../MainInput/MainInput';
import Sider from '../Sider/Sider';
import EditableTable from '../Table/TableDataColums';
import UpTable from '../UpTable/UpTable';
import { Input } from 'antd';
import './Layout.css';
import Uppload from '../Uppload/Uppload';
import ButtonSize from '../Download/Download';
import IdInput from '../IdInput/IdInput';
import http from "../http-common";
import Modal from '../Modal/Modal';
var fileDownload = require('react-file-download');




class Layout extends Component {
    constructor(props) {
        super(props);

        this.style = {
            display: props.display
        }

        this.state = {
            templateId: '',
            resultArray: [],
            zipFile: null,
            show: false
        }
    }

    showModal = () => {
        this.setState({ show: true });
    };

    hideModal = () => {
        this.setState({ show: false });
    };

    setTemplateId = (value) => {
        this.setState({ templateId: value });
    }
    setResultArray = (resultArray) => {
        this.setState({ resultArray });
        if(this.state.resultArray.length>0){
            this.showModal();
        }
    }
    printTemplateId = () => {
        console.log(this.state);
        http.post("/prejson", { data: this.state }, { responseType: "arraybuffer" }
        ).then(res => {
            console.log(res);
            console.log(res.data);
            fileDownload(res.data, 'filename.zip');
            // const url = new Blob([res.data],{type:'application/zip'});
            // console.log(url);
            // const link = document.createElement('a');
            // link.href = url;

            // link.setAttribute('download', 'file.zip');
            // console.log(link);
            // document.body.appendChild(link);
            // link.click();

        })
        console.log(this.state.zipFile);
        //  if(this.state.zipFile!=null){
        //     fileDownload(this.state.zipFile, "file.zip");
        //  }
    };


    // setFinalArray=(array)=>{
    //        this.finalArray = array;
    //        console.log(this.finalArray);
    // }
    // handleChange=(e)=> {
    //     const files = e.target.files;
    //     if (files && files[0]) this.props.handleFile(files[0]);
    //   }


    render() {
        return (

            <div style={this.style}>
                <header ></header>

                <div className="asmain">

                    <aside >
                        <Sider />
                    </aside>
                    <main>
                        {/* <br/>
            <h3>Choose Fund</h3>
            <br/>
            <div className="mainInput">
                <MainInput />
                <Input placeholder="New fund name"  style={{
                        marginLeft: 20,
                        width: 200,
                    }}/>
            </div>
            <br/>
            <h3>Choose document type</h3>
            <br/>
            <div className="tableup" >
                <UpTable />
                </div>

            <h3>Specify data columns</h3>
            <br/>          
                <div className="tableedit">
                <EditableTable />
                </div> */}
                        <br />
                        <h3>Upload Excel/CSV with data</h3>
                        <br />
                        <div className="uppload">
                            <Uppload onUpload={this.setResultArray} />
                            <Modal show={this.state.show} handleClose={this.hideModal}>
                                <p>Modal</p>
                            </Modal>
                        </div>

                        <br />
                        <h3>Data Preview</h3>
                        <br />



                        <br />
                        <h3>Input PDF template #</h3>
                        <br />

                        <div className="pdftemplate">
                            <IdInput setTemplateId={this.setTemplateId} />
                        </div>



                        <br />
                        <div className="download" style={{
                            marginLeft: 20,
                        }}>
                            <ButtonSize onClick={this.printTemplateId} disabled={this.state.templateId.length !== 6 || this.state.resultArray.length === 0} />
                        </div>


                    </main>
                </div>
            </div>


        )

    }
}




export default Layout;
